import User from '../models/user.model.js';
import bcryptjs from 'bcryptjs';
import { errorHandler } from '../utils/error.js';
import jwt from 'jsonwebtoken';
import { sendVerificationEmail } from '../utils/email.js';

import { getVerificationData, saveVerificationCode, deleteVerificationCode } from '../utils/verificationStore.js';


export const signup = async (req, res, next) => {
  const { username, email, password, repeatpassword } = req.body;

  if (!username || !email || !password || !repeatpassword) {
    return res.status(400).json({ message: "All fields are required" });
  }

  if (password !== repeatpassword) {
    return res.status(400).json({ message: "Passwords do not match" });
  }

  const hashedPassword = bcryptjs.hashSync(password, 10);

  const newUser = new User({
    username,
    email,
    password: hashedPassword,
    avatar: "data:image/png;base64,", // âœ… Default avatar
  });

  try {
    await newUser.save();
    res.status(201).json("User created successfully");
  } catch (error) {
    console.error("Signup error:", error);
    next(error);
  }
};

export const verifyAndRegister = async (req, res) => {
  const { email, code } = req.body;

  if (!email || !code) {
    return res.status(400).json({ message: 'Email and code are required' });
  }

  const entry = getVerificationData(email);

  if (!entry) {
    return res.status(400).json({ message: 'No verification data found. Please request a new code.' });
  }

  if (Date.now() > entry.expiresAt) {
    deleteVerificationCode(email);
    return res.status(400).json({ message: 'Code expired. Please request a new one.' });
  }

  if (entry.code !== code) {
    return res.status(400).json({ message: 'Invalid verification code.' });
  }

  const { username, password } = entry.userData;

  const hashedPassword = bcryptjs.hashSync(password, 10);

  const newUser = new User({
    username,
    email,
    password: hashedPassword,
     avatar: "data:image/png;base64,", // âœ… Default avatar
  });

  try {
    await newUser.save();
    deleteVerificationCode(email); // cleanup
    res.status(201).json({ message: 'User registered successfully.' });
  } catch (err) {
    res.status(500).json({ message: 'User creation failed.' });
  }
};

export const signin = async (req, res, next) => {
  const { email, password } = req.body;

  // Check required fields
  if (!email || !password) {
    return res.status(400).json({ message: "Email and password are required" });
  }

  try {
    const user = await User.findOne({ email });

    if (!user) {
      return next(errorHandler(404, "User not found"));
    }

    const isPasswordValid = bcryptjs.compareSync(password, user.password);

    if (!isPasswordValid) {
      return next(errorHandler(401, "Wrong Credentials!"));
    }

    const { password: pwd, ...userData } = user._doc;

    const token = jwt.sign({ id: user._id }, process.env.JWT_SECRET, { expiresIn: "1h" });

    res.cookie("access_token", token, {
      httpOnly: true,
    }).status(200).json(userData); // âœ… send plain userData directly (no { user: ... })

  } catch (error) { 
    next(error);
  }
};



